import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEye } from '@fortawesome/fontawesome-free-solid'

const Quizes = ({ ques }) => {

    const { correctAnswer, id, options, question } = ques;
    const [a, b, c, d] = options;
    const showCorrectAns = (id) => {
        <h5>correctAnswer:{correctAnswer}</h5>
    }
    return (
        <div className='p-4'>
            <div className='border p-4 rounded-2 w-75'>
                <div className='text-end'><button><FontAwesomeIcon icon={faEye}></FontAwesomeIcon></button></div>

                <h3>Ques:{question}</h3>
                <div className='row row-cols-2'>
                    <p> <input type='radio' name='quiz' />&nbsp;{a}</p>
                    <p> <input type='radio' name='quiz' />&nbsp;{b}</p>
                    <p> <input type='radio' name='quiz' />&nbsp;{c}</p>
                    <p> <input type='radio' name='quiz' />&nbsp;{d}</p>

                </div>
            </div>
        </div >

    );
};

export default Quizes;
