import React from 'react';
import { useLoaderData } from 'react-router-dom';
import { Bar, BarChart, CartesianGrid, Legend, Tooltip, XAxis, YAxis } from 'recharts';

const StatChart = () => {
    const topics = useLoaderData();
    const { data } = topics;
    console.log(data[1].total);
    console.log(data[0].name);

    const data1 = [
        {
            name: data[0].name,
            uv: 4000,
            pv: 2400,
            amt: 2400
        },
        {
            name: data[0].name,
            uv: 3000,
            pv: 1398,
            amt: 2210
        },
        {
            name: data[0].name,
            uv: 2000,
            pv: 9800,
            amt: 2290
        },
        {
            name: data[0].name,
            uv: 2780,
            pv: 3908,
            amt: 2000
        }];
    return (
        <div>
            <BarChart
                width={500}
                height={300}
                data1={data1}
                margin={{
                    top: 5,
                    right: 30,
                    left: 20,
                    bottom: 5
                }}
            >
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Bar dataKey="pv" fill="#8884d8" />
                <Bar dataKey="uv" fill="#82ca9d" />
            </BarChart>
        </div>
    );
};

export default StatChart;
