import React from 'react';
import { useLoaderData } from 'react-router-dom';
import { Bar, BarChart, CartesianGrid, Legend, Tooltip, XAxis, YAxis } from 'recharts';

const StatChart = () => {
    const topics = useLoaderData();
    const { data } = topics;
    const data1 = [
        {
            name: `${data[0].name}`,
            React_totalQuiz: `${data[0].total}`,
        },
        {
            name: `${data[1].name}`,
            JavaScript_totalQuiz: `${data[1].total}`,
        },
        {
            name: `${data[2].name}`,
            CSS_totalQuiz: `${data[2].total}`,
        },
        {
            name: `${data[3].name}`,
            Git_totalQuiz: `${data[3].total}`,
        }];
    return (
        <div>
            <BarChart width={500} height={350} data={data1}>
                <CartesianGrid strokeDasharray="0 7" />
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Bar dataKey="React_totalQuiz" fill="blue" />
                <Bar dataKey="JavaScript_totalQuiz" fill="green" />
                <Bar dataKey="CSS_totalQuiz" fill="tomato" />
                <Bar dataKey="Git_totalQuiz" fill="purple" />
            </BarChart>
        </div>
    );
};

export default StatChart;
