import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCheckSquare, faCoffee } from '@fortawesome/fontawesome-free-solid'

const Quizes = ({ ques }) => {

    const { correctAnswer, options, question } = ques;
    const [a, b, c, d] = options;
    return (
        <div className='p-4'>
            <div className='border p-4 rounded-2 w-75'>
                <h3>Ques:{question}</h3>
                <FontAwesomeIcon></FontAwesomeIcon>
                <div className='row row-cols-2'>
                    <p> <input type='radio' name='quiz' />&nbsp;{a}</p>
                    <p> <input type='radio' name='quiz' />&nbsp;{b}</p>
                    <p> <input type='radio' name='quiz' />&nbsp;{c}</p>
                    <p> <input type='radio' name='quiz' />&nbsp;{d}</p>

                </div>
            </div>
            <div><h5>correctAnswer:{correctAnswer}</h5></div>
        </div>

    );
};

export default Quizes;
