import React from 'react';
import { useLoaderData } from 'react-router-dom';

const Quizes = ({ ques }) => {
    const datum = useLoaderData();
    const { id } = datum.data;
    console.log(datum);
    console.log(id);
    const { correctAnswer, options, question } = ques;
    const [a, b, c, d] = options;
    return (
        <div className='border m-3'>
            <h3>Ques{id}:{question}</h3>
            <p>a.{a}</p>
            <p>b.{b}</p>
            <p>c.{c}</p>
            <p>d.{d}</p>
            <div><h5>correctAnswer:{correctAnswer}</h5></div>
        </div>

    );
};

export default Quizes;
