import React from 'react';
import { useLoaderData } from 'react-router-dom';
import { Bar, BarChart, CartesianGrid, Legend, Tooltip, XAxis, YAxis } from 'recharts';

const StatChart = () => {
    const topics = useLoaderData();
    const { data } = topics;
    console.log(data[1].total);
    console.log(data[0].name);

    const data1 = [
        {
            name: `${data[0].name}`,
            uv1: `${data[0].total}`,
        },
        {
            name: `${data[1].name}`,
            uv2: `${data[1].total}`,
        },
        {
            name: `${data[2].name}`,
            uv3: `${data[2].total}`,
        },
        {
            name: `${data[3].name}`,
            uv4: `${data[3].total}`,
        }];
    return (
        <div>
            <BarChart width={730} height={250} data={data1}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Bar dataKey="uv1" fill="#82ca9d" />
                <Bar dataKey="uv2" fill="#82ca9d" />
                <Bar dataKey="uv3" fill="#82ca9d" />
                <Bar dataKey="uv4" fill="#82ca9d" />
            </BarChart>
        </div>
    );
};

export default StatChart;
