import React from 'react';


const Quizes = ({ ques }) => {

    const { correctAnswer, options, question } = ques;
    const [a, b, c, d] = options;
    return (
        <div className=' m-3 '>
            <div className='border p-4'>
                <h3>Ques:{question}</h3>
                <p>a.{a}</p>
                <p>b.{b}</p>
                <p>c.{c}</p>
                <p>d.{d}</p>
            </div>
            <div><h5>correctAnswer:{correctAnswer}</h5></div>
        </div>

    );
};

export default Quizes;
