import React from 'react';
import { useLoaderData } from 'react-router-dom';
import { Bar, BarChart, CartesianGrid, Legend, Tooltip, XAxis, YAxis } from 'recharts';

const StatChart = () => {
    const topics = useLoaderData();
    const { data } = topics;
    console.log(data[1].total);
    console.log(data[0].name);

    return (
        <div>
            <div>
                <BarChart width={730} height={250} data={data}>
                    <CartesianGrid strokeDasharray="4 4" />
                    <XAxis dataKey={data[0].name} />
                    <XAxis dataKey={data[1].name} />
                    <XAxis dataKey={data[2].name} />
                    <XAxis dataKey={data[3].name} />
                    <YAxis dataKey={data[0].total} />
                    <YAxis dataKey={data[1].total} />
                    <YAxis dataKey={data[2].total} />
                    <YAxis dataKey={data[3].total} />
                    <Tooltip />
                    <Legend />
                    <Bar dataKey={name} fill="blue" />
                    <Bar dataKey={name} fill="orange" />
                </BarChart>
            </div>
        </div>
    );
};

export default StatChart;
